+ if (currentDifficulty === 'easy' && easy !== null && easy > 85 && interactionsByComplexity.easy > 5) {
+ targetDifficulty = 'medium';
+ reason = 'You\'re performing very well on easy questions';
+ } else if (currentDifficulty === 'medium' && medium !== null) {
