+ targetDifficulty = 'hard';
+ reason = 'Your high accuracy shows you\'re ready for more challenges';
+ } else if (accuracy < 40) {
+ targetDifficulty = 'easy';
+ reason = 'Starting with easier questions will help build your confidence';
+ }
+ }
